var btnHotelsPopup=document.querySelector("#btn-hotels-popup"),popup=document.querySelector(".form-hotels"),arrival=document.querySelector("[name=arrival]");btnHotelsPopup.addEventListener("click",function(e){e.preventDefault(),popup.classList.contains("modal-hide")?(popup.classList.remove("modal-hide"),arrival.focus()):popup.classList.add("modal-hide")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(popup.classList.contains("modal-hide")||(e.preventDefault(),popup.classList.add("modal-hide")))});